class Typewriter{constructor(n){this.typewriterText=document.querySelector(n);this.receivedText=this.typewriterText.textContent;this.textInterval=null;this.i=0}clearText(){this.typewriterText.innerHTML=""}typeText(){this.typewriterText.innerHTML+=this.receivedText[this.i];this.i++;this.i===this.receivedText.length&&clearInterval(this.textInterval)}randomInteger(n,t){return Math.round(n-.5+Math.random()*(t-n+1))}startTyping(){this.clearText();this.textInterval=setInterval(()=>this.typeText(),this.randomInteger(50,200))}}const typewriter1=new Typewriter(".typewriter-text");typewriter1.startTyping();const typewriter2=new Typewriter(".typewriter-text2");typewriter2.startTyping();